mapper.insert:
INSERT INTO ANSWER 	(
						ANSWER_ID
						, ANSWER_DATE
						, ANSWER_CONTENT
						, MEMBER_ID
						, QUESTION_ID
					)
VALUES				(
						ANSWER_ID_SEQ.NEXTVAL
						, SYSDATE
						, #{answerContent}
						, #{memberId}
						, #{questionId}
					)

mapper.update:
UPDATE 	ANSWER
SET		ANSWER_CONTENT = #{answerContent}
WHERE	ANSWER_ID = #{answerId}
AND		QUESTION_ID = #{questionId}

UPDATE 	ANSWER
SET		ANSWER_CONTENT = '삭제된 댓글입니다.(' || SYSDATE || ')'
WHERE	ANSWER_ID = #{answerId}
AND		QUESTION_ID = #{questionId}
